package com.wonder.chatSample.webSocket.utility;import com.wonder.chatSample.webSocket.model.WebsocketMessage;import javax.websocket.EncodeException;import javax.websocket.Session;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Component;import java.io.IOException;@Slf4j@Componentpublic class WebSocketTextMessageHelper {    public void send(Session session, WebsocketMessage webSocketMessage) throws EncodeException, IOException {        try {            log.info("Sending message of type '{}'.", webSocketMessage.getMessageType());            session.getBasicRemote().sendObject(webSocketMessage);        }catch (IOException | EncodeException e){            log.error("WebSocket error, message {} was not sent.", webSocketMessage, e);        }    }}